mapper.select:
select
count(distinct pkg.package_id)
from
t_wine wine
inner join t_product prod on wine.wine_id =prod.wine_id
inner join t_wine_type wty on wine.wine_type_id=wty.wine_type_id
inner join t_wine_vintage vtg on prod.vintage_tag=vtg.vintage_tag
inner join t_package pkg on prod.product_id=pkg.product_id
inner join t_origin ori on wine.wine_origin_id=ori.wine_origin_id
inner join t_country cty on ori.country_id=cty.country_id
where pkg.regular_price > #{priceMin}
and pkg.regular_price < #{priceMax}

  and prod.supplier_id=#{supplierId}


  and wine.wine_type_id=#{wineTypeId}


  and ori.country_id=#{countryId}


  and wine.wine_origin_id=#{wineOriginId}

select DISTINCT pkg.package_id, wine.wine_id, wine.wine_name_eng, wty.wine_type_name_eng, ori.region_name_eng, cty.country_name_eng, vtg.vintage_name, pkg.regular_price, pkg.glass_price, wine.wine_img_id from t_wine wine inner join t_product prod on wine.wine_id =prod.wine_id inner join t_wine_type wty on wine.wine_type_id=wty.wine_type_id inner join t_wine_vintage vtg on prod.vintage_tag=vtg.vintage_tag inner join t_package pkg on prod.product_id=pkg.product_id inner join t_origin ori on wine.wine_origin_id=ori.wine_origin_id inner join t_country cty on ori.country_id=cty.country_id where pkg.regular_price > #{priceMin} and pkg.regular_price < #{priceMax}
and prod.supplier_id=#{supplierId}
and wine.wine_type_id=#{wineTypeId}
and ori.country_id=#{countryId}
and wine.wine_origin_id=#{wineOriginId}
limit #{offset,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
