mapper.select:
select count(distinct pkg.package_id) FROM t_package pkg INNER JOIN t_product prd ON pkg.product_id = prd.product_id INNER JOIN t_wine_vintage winvtg ON prd.wine_id = winvtg.wine_id AND prd.vintage_tag = winvtg.vintage_tag INNER JOIN t_wine win ON prd.wine_id = win.wine_id INNER JOIN t_wine_type wintype ON win.wine_type_id = wintype.wine_type_id LEFT JOIN t_winery winry ON win.winery_id = winry.winery_id LEFT JOIN t_origin org ON win.wine_origin_id = org.wine_origin_id LEFT JOIN t_country ctry ON org.country_id = ctry.country_id LEFT JOIN t_wine_vintage_attr winattr ON win.wine_id = winattr.wine_id AND prd.vintage_tag = winattr.vintage_tag LEFT JOIN t_attr attr ON winattr.attr_id = attr.attr_id AND attr.attr_catg_id = 101 WHERE prd.supplier_id = #{supplierId}
AND pkg.regular_price >=#{priceMin}
AND pkg.regular_price <=#{priceMax}
AND win.wine_type_id IN
#{wineTypeId,jdbcType=BIGINT}
AND org.country_id IN
#{countryId,jdbcType=BIGINT}
AND attr.attr_id IN
#{attrId,jdbcType=BIGINT}
AND win.wine_origin_id IN
#{wineOriginId,jdbcType=BIGINT}
AND pkg.glass_price <> 0.00
AND winvtg.is_organic_wine = 1

SELECT DISTINCT pkg.package_id, pkg.regular_price, pkg.glass_price, prd.wine_id, prd.supplier_id, win.wine_name_eng, win.wine_name_chs, win.wine_name_cht, wintype.wine_type_name_eng, winry.winery_id, winry.winery_name_eng, winry.logo_img_id AS winery_logo_id, org.region_name_eng, org.region_name_chs, org.region_name_cht, ctry.country_name_eng, ctry.country_name_chs, ctry.country_name_cht, winvtg.vintage_tag, winvtg.vintage_name, win.wine_img_id FROM t_package pkg INNER JOIN t_product prd ON pkg.product_id = prd.product_id INNER JOIN t_wine_vintage winvtg ON prd.wine_id = winvtg.wine_id AND prd.vintage_tag = winvtg.vintage_tag INNER JOIN t_wine win ON prd.wine_id = win.wine_id INNER JOIN t_wine_type wintype ON win.wine_type_id = wintype.wine_type_id LEFT JOIN t_winery winry ON win.winery_id = winry.winery_id LEFT JOIN t_origin org ON win.wine_origin_id = org.wine_origin_id LEFT JOIN t_country ctry ON org.country_id = ctry.country_id LEFT JOIN t_wine_vintage_attr winattr ON win.wine_id = winattr.wine_id AND prd.vintage_tag = winattr.vintage_tag LEFT JOIN t_attr attr ON winattr.attr_id = attr.attr_id AND attr.attr_catg_id = 101 WHERE prd.supplier_id = #{supplierId}
AND pkg.regular_price >=#{priceMin}
AND pkg.regular_price <=#{priceMax}
AND win.wine_type_id IN
#{wineTypeId,jdbcType=BIGINT}
AND org.country_id IN
#{countryId,jdbcType=BIGINT}
AND attr.attr_id IN
#{attrId,jdbcType=BIGINT}
AND win.wine_origin_id IN
#{wineOriginId,jdbcType=BIGINT}
AND pkg.glass_price <> 0.00
AND winvtg.is_organic_wine = 1
limit #{offset,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

mapper.delete:
delete
from t_package
where product_id = #{productId}
 and volume_type_id = #{volumeTypeId}
