mapper.select:
select 

from t_wine_attr
where attr_id = #{attrId,jdbcType=BIGINT}
  and wine_id = #{wineId,jdbcType=BIGINT}

mapper.delete:
delete from t_wine_attr
where attr_id = #{attrId,jdbcType=BIGINT}
  and wine_id = #{wineId,jdbcType=BIGINT}

mapper.update:
update t_wine_attr


    mic_rank = #{micRank,jdbcType=SMALLINT},


    updated_at = #{updatedAt,jdbcType=TIMESTAMP},


where attr_id = #{attrId,jdbcType=BIGINT}
  and wine_id = #{wineId,jdbcType=BIGINT}

update t_wine_attr
set mic_rank = #{micRank,jdbcType=SMALLINT},
  updated_at = #{updatedAt,jdbcType=TIMESTAMP}
where attr_id = #{attrId,jdbcType=BIGINT}
  and wine_id = #{wineId,jdbcType=BIGINT}
