mapper.delete:
delete from t_wine_vintage_score
where wine_id = #{wineId,jdbcType=BIGINT}
and vintage_tag = #{vintageTag,jdbcType=BIGINT}

mapper.select:
select

from t_wine_vintage_score
  where wine_id = #{wineId,jdbcType=BIGINT}
  and vintage_tag = #{vintageTag,jdbcType=BIGINT}
  and score_val_num is not null

select

from t_wine_vintage_score
where wine_id = #{wineId,jdbcType=BIGINT}
and vintage_tag = #{vintageTag,jdbcType=BIGINT}
and score_name is not null
